export var ENV = {
  online: "https://mapi.zjzwfw.gov.cn",
  pre: "//pre-mapi.zjzwfw.gov.cn",
  daily: "//47.96.150.251:8068"
};

var getHostByEnv = function (env, host) {
  if (host) {
    if (/\/$/.test(host)) {
      host = host.slice(0, -1);
    }

    return host;
  }

  return ENV[env] || ENV.online;
};

export default getHostByEnv;